//      props.history.push("api/order");
//     console.log("Order Received");
//    };
   const checkoutHandler = async () => {
     const response = await axios({
       method: "POST",
       url: "http://localhost:4000/api/order",
       data: {
         qty: qty,
         productId: productId,
         amount: products.amount,

       },
     }).then((response) => {
