         });
   };

  return (
     <div>
       <div>
         <h1> Cart</h1>
         <h1> ProductId: {productId} qty:${qty} </h1>


           <ul>
             {cartItems.map((item) => (
               <li key={item.product}>
                 <div >

                   <div >
                  {item.name}
                   </div>
                   <div>
                     <select>
                       value={item.qty}
                       onChange={(e) =>
                         dispatch(
                           addToCart(item.productId, Number(e.target.value))

                         )
                       }
                       {[...Array(100).keys()].map((x) => (
                         <option key={x + 1} value={x + 1}>
                           {x + 1}
                         </option>
                       ))}

                     </select>
                   </div>
                   <div>
                    Rs. {item.amount}</div>

                 </div>
                 <div>
                    <button onClick = {() =>  removeHandler(item.productId)}>
                    Remove From Cart
                    </button>
                  </div>
               </li>
             ))}
           </ul>

       </div>
       <div className='col-1'>
        <div className='card'>
            <h1>Subtotal {cartItems.reduce((a,x) => a+x.qty,0 )} Items:
            Rs.  {cartItems.reduce((a,x) => a + x.qty * x.amount, 0)}

            </h1>
              <button onClick={checkoutHandler} disabled={cartItems.length === 0}>
                Proceed To Checkout
              </button>
        </div>


       </div>
     </div>
   );
 }

























































































































