import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import { useStateValue } from "./StateProvider";
// import Modal from 'react-modal';
import { Modal, Button } from "antd";
import Mod from "./Model";

function Header() {
  const [{ cart }, dispatch] = useStateValue();
  const [isModalVisible, setIsModalVisible] = useState(false);

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div className="header">
      <Link to="/">
        <img
          className="header_logo"
          src="data:image/png;base64,"
        />
      </Link>

      <div className="header_search">
        <input className="header_searchInput" type="text" />

        <div className="header_nav">
          <div className="header_option">
            <span className="header_option_LineOne">Hello </span>
            <Link>
              <span className="header_option_LineTwo"> User</span>
            </Link>
          </div>

          <div className="header_option">
            <span className="header_option_LineOne">Create</span>
            <span className="header_option_LineTwo">Category</span>
            <Button type="primary" onClick={() => setIsModalVisible(true)}>
              Create Category
            </Button>
            {/* {isModalVisible && (
              <Mod handleOk={handleOk} handleCancel={handleCancel} />
            )} */}
          </div>

          <Link to="/Order">
            <div className="header_option">
              <span className="header_option_LineOne">My</span>
              <span className="header_option_LineTwo">Orders</span>
            </div>
          </Link>

          <Link to="/Checkout">
            <div className="header_optionBasket">
              <span className="header_option_LineOne">My Cart </span>
              <span className="header_option_LineTwo">{cart.length}</span>
            </div>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Header;
