import React, { useEffect, useState  } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import axios from '../../node_modules/axios/index';
import { detailsProduct } from '../actions/productActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import Rating from '../components/Rating';


export default function ProductScreen(props) {
  const [qty, setQty] = useState(1);
  const dispatch = useDispatch();
  const productId = props.match.params.id;
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, products } = productDetails;
  console.log(props.match.params)
  useEffect(() => {
    console.log(productId)
    dispatch(detailsProduct(productId));
  }, [dispatch, productId]);



  const addToCartHandler = async() => {

    const response = await axios({
      method: "POST",
      url: "http://localhost:4000/api/cart",
      data: {
        qty: qty,
       productId: productId,
       amount: products.amount,


      },
    }).then(
      (response) => {
        props.history.push(`http://localhost:4000/api/cart/${productId}?qty=${qty}`);
        console.log(response);
      },
    )
  };


  return (
 <div>
 {loading ? (
  <LoadingBox></LoadingBox>
) : error ? (
  <MessageBox variant="danger">{error}</MessageBox>
) : (
  <div>
    <Link to="/">Back to result</Link>
    <div className="row top">
      <div className="col-2">

      </div>
      <div className="col-1">
        <ul>
          <li>
            <h1>Name: {products.name}</h1>
          </li>
          <li>
            <h1>{products.amount}</h1>
          </li>
          <li>
            <Rating
              // ratings={products.ratings}
             // numReviews={product.numReviews}
            ></Rating>
          </li>
          <li>Price : ${products.amount}</li>

        </ul>
      </div>
      <div className="col-1">
        <div className="card card-body">
          <ul>
            <li>
              <div className="row">
                <div>Price</div>
                <div className="price">Rs. {products.amount}</div>
              </div>
            </li>



              <div>
                <li>
                  <div className="row">
                    <div>No.of Items</div>
                    <div>
                      <select
                        value={qty}
                        onChange={(e) => setQty(e.target.value)} >

                        {[...Array(100).keys()].map(
                          (x) => (
                            <option key={x + 1} value={x + 1}>
                              {x + 1}
                            </option>
                          )
                        )}
                      </select>
                    </div>
                  </div>
                </li>
                <li>
                  <button
                    onClick={addToCartHandler}
                    className="primary block">

                    Add to Cart
                  </button>
                </li>

                </div>

          </ul>

        </div>
      </div>
          </div>
        </div>
     )}
    </div>
  );
}

