    setIsModalVisible(false);
  };

  return (
    <>
      <Button type="primary" onClick={showModal}>
        Create Product
      </Button>

      <Modal
        title="Create Product"
        visible={isModalVisible}
        onOk={handleOk}
        okText="Submit"
        onCancel={handleCancel}
      >
        <h1>
          <>

          <Form
      // {...formItemLayout}
      // layout={formLayout}
      // form={form}
      // initialValues={{
      //   layout: formLayout,
      // }}
      // onValuesChange={onFormLayoutChange}dfd
      >
        <Form.Item label="Product Name">
          <Input
            value={value}
            placeholder="Type in Product Name"
            onChange={Prod}
          />
        </Form.Item>
      </Form>

      <Form>
        <Form.Item label="Amount">
          <Input
            value={value1}
            placeholder="Type in Product Price"
            onChange={Amount}
          />
        </Form.Item>

        <Form.Item label="No.of Units">
          <Input
            value={value2}
            placeholder="Type in Units"
            onChange={Units}
          />
        </Form.Item>

        <Form.Item label="Spec-1">

        </Form.Item>

        <Form.Item label="Name">
          <Input placeholder="input placeholder" onChange={Spec}/>
        </Form.Item>

        <Form.Item label="Value">
          <Input placeholder="input placeholder" onChange={S2Change} />
        </Form.Item>
























        </Form>






































