    setValue1(e.target.value);
    props.updateProductPrice(value1);
  };

  const ratingsChange = (e) => {
    setValue2(e.target.value);
    props.updateProductRatings(value2);
  };

  const formItemLayout = {
    labelCol: {
      span: 11,
    },
    wrapperCol: {
