import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import SearchList from './SearchList';

const SearchMaster = (props) => {
  const [input, setInput] = useState('');
  const [searchListDefault, setSearchListDefault] = useState();
  const [searchList, setSearchList] = useState();

  const fetchData = async () => {
    return await fetch("/api/products")
      .then(response => response.json())
      .then(data => {
         setSearchList(data) 
         setSearchListDefault(data)
       });}

  const updateInput = async (input) => {
     const filtered = searchListDefault.filter(product => {
      return product.name.toLowerCase().includes(input.toLowerCase())
     })
     setInput(input);
     setSearchList(filtered);
  }

  useEffect( () => {fetchData()},[]);

  return (
    <>
      <h1>List of Products</h1>
      <SearchBar 
       input={input} 
       onChange={updateInput}
      />
      <CountryList searchList={searchList}/>
    </>
   );
}

export default SearchMaster;
