import React, { useState } from "react";
import { Modal, Button } from "antd";
import Catmod3 from './Catmod3'
import DropCat from "./DropCat";
import DropBrand from "./DropBrand";
import GetProd from "./axprod";
import onChange from "./Catmod3";

const Prodmod = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const [value, setValue] = useState();
  const [value1, setValue1] = useState();
  const [value2, setValue2] = useState();
  const [value3, setValue3] = useState();


  const Prod = (e) => {
    setValue(e);
  };

  const Amount = (e) => {
    setValue1(e);
  };

  const Units = (e) => {
    setValue2(e);
  };

  const Spec = (e) => {
    setValue3(e);
  };


  const handleOk = () => {
    setIsModalVisible(false);
    console.log(value)
    console.log(value1);
    console.log(value2);

    GetProd({name:value,amount:value1, units:value2, spec1:value3, spec2:value4, spec3:value5});

    // console.log("Successfully Added");
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <>
      <Button type="primary" onClick={showModal}>
        Create Product
      </Button>

      <Modal
        title="Create Product"
        visible={isModalVisible}
        onOk={handleOk}
        okText="Submit"
        onCancel={handleCancel}
      >
        <h1>
          <>
            <Catmod3 updateProductName={Prod} updateProductAmount={Amount} updateProductUnits={Units} updateProductSpec1={Spec1} updateProductSpec1={Spec1} updateProductSpec1={Spec1}/>
            <DropCat/>
            <DropBrand/>
          </>
        </h1>

      </Modal>
    </>
  );
};

export default Prodmod;
