import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

import { addToCart, removeFromCart } from "../actions/cartActions";

export default function CartScreen(props) {
  const productId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split("=")[1])
    : 1;

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  const dispatch = useDispatch();
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeHandler = (productId) => {
    dispatch(removeFromCart(productId));
  };

  const checkoutHandler = async () => {
    const response = await axios({
      method: "POST",
      url: "http://localhost:4000/api/order",
      data: {
        qty: qty,
        productId: productId,
      },
    }).then((response) => {
      props.history.push(`/order`);
    });
  };

  return (
    <div>
      <div>
        <h1> Cart</h1>
        <h1>
          ProductId: {productId} Quantity:{qty}
        </h1>

        <ul>
          {cartItems.map((item) => (
            <li key={item.product}>
              <div>
                <div>{item.name}</div>
                <div>
                  <select>
                    value={item.qty}
                    onChange=
                    {(e) =>
                      dispatch(
                        addToCart(item.productId, Number(e.target.value))
                      )
                    }
                    {[...Array(x=100).keys()].map((x) => (
                      <option key={x + 1} value={x + 1}>
                        {x + 1}
                      </option>
                    ))}
                  </select>
                </div>
                <div>Rs. {item.amount}</div>
              </div>
              <div>
                <button onClick={() => removeHandler(item.product)}>
                  Remove From Cart
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="col-1">
        <div className="card">
          <h1>
            Subtotal {cartItems.reduce((a, x) => a + x.qty, 0)} Items: Rs.{" "}
            {cartItems.reduce((a, x) => a + x.qty * x.amount, 0)}
          </h1>
          <button onClick={checkoutHandler} disabled={cartItems.length === 0}>
            Order Now
          </button>
        </div>
      </div>
    </div>
  );
}

