import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import axios from "../../node_modules/axios/index";
import { addToCart, removeFromCart } from "../actions/cartActions";
import { createOrder } from "../actions/orderAction";
import MessageBox from "../components/MessageBox";

export default function CartScreen(props) {
  const productId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split("=")[1])
    : 1;
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;
  // const [items, setItems] = useState();
  // const { data } = await axios.get("/api/cart/findall");
  // setItems(data);

  const dispatch = useDispatch();
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeHandler = (productId) => {
     dispatch(removeFromCart(productId));
   };

  // const checkoutHandler = () => {
  //   props.history.push("/orders/${order.id}");
  //   placeOrderHandler();
  //   console.log("Order Received");
  // };
  const checkoutHandler = async () => {
    const response = await axios({
      method: "POST",
      url: "http://localhost:4000/api/order",
      data: {
        qty: qty,
        productId: productId,
      },
    }).then((response) => {
      props.history.push(`/order`);
    });
  };

  // const placeOrderHandler = async() => {
  // dispatch(createOrder({ ...cart, orderItems: cart.cartItems }))};

  return (
    <>
      <div>

         ProdcutId: {productId} Quantity: {qty}
      </div>
      <div className="row top">
        <div className="col-2">
          <h1> Cart</h1>
          {cartItems.length === 0 ? (
            <MessageBox>
              Empty Cart. <Link to="/">Back to HomeScreen</Link>
            </MessageBox>
          ) : (
            <ul>
              {cartItems.map((item) => (
                <li key={item.product}>
                  <div className="row">
                    <div>
                      <Link
                        to={`http://localhost:4000/api/products/${item.product}`}
                      >
                        {item.name}
                      </Link>
                    </div>
                    <div>
                      <select
                        value={item.qty}
                        onChange={(e) =>
                          dispatch(
                            addToCart(item.product, Number(e.target.value))
                          )
                        }
                      >
                        {[...Array(100).keys()].map((x) => (
                          <option key={x + 1} value={x + 1}>
                            {x + 1}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>${item.amount}</div>
                    <div>
                      <button
                        type="button"
                        onClick={() => removeHandler(item.product)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div className="">
          <div >
            <ul>
              <li>
                <h2>
                  Subtotal ({cartItems.reduce((a, c) => a + c.qty, 0)} items) :
                  ${cartItems.reduce((a, c) => a + c.amount * c.qty, 0)}
                </h2>
              </li>
              <li>
                <button type="button" onClick={checkoutHandler}>
                  Proceed to Checkout
                </button>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </>
  );
}
