    const itemHistory =() => {

        props.history.push('/orderItems');
    }

  return (
    <>
      <div>
        <h1>
          <strong>Your Order Summary</strong>
          <br></br>You have ordered successfully
        </h1>
        <div>
          {items.map(
            (item) => 
          item.id)}


        </div>
      </div>

      <div>
          <button  onClick={itemHistory}>
              Order History
          </button>
      </div>
    </>
  );
}

