import React from 'react';
import { useSelector } from 'react-redux';
import { BrowserRouter, Link, Route } from 'react-router-dom';
import CartScreen from './screens/CartScreen';
import HomeScreen from './screens/HomeScreen.js';
import ProductScreen from './screens/ProductScreen.js';
import OrderScreen from './screens/OrderScreen';

import Mod from "./Model";
import Brandmod from './Brandmod';
import Prodmod from './Prodmod';
import OrderItems from './screens/OrderItems';
function App() {
  const cart = useSelector(state => state.cart);
  const { cartItems } = cart;
  return (
    <BrowserRouter>
    <div className="grid-container">
      <header className="row">
        <div>
        <Link className="brand" to="/">e-Comm</Link>
        </div>
        {/* <div><Link to="/">
        <img
          className="header_logo"
          src="data:image/png;base64,"
        />
      </Link></div> */}

        <div className="header_option">
        <span className="header_option_LineOne"></span>
        <span className="header_option_LineTwo"><Mod /></span>
      </div>

      <div className="header_option">
          <span className="header_option_LineOne"></span>
          <span className="header_option_LineTwo"><Brandmod/></span>
        </div>

        <div className="header_option">
        <span className="header_option_LineOne"></span>
        <span className="header_option_LineTwo"><Prodmod/></span>
      </div>


        <div>

          <Link to="/cart">
          Cart -
             {
            <span className="badge">{cartItems.length}</span>
          }
        </Link>
        </div>
     </header>
      <main>
      <Route path="/cart/:id?" component={CartScreen}></Route>
      <Route path="/products/:id" component={ProductScreen}></Route>
      <Route path="/order/:id?" component={OrderScreen}></Route>
      <Route path="/" component={HomeScreen} exact></Route>
      <Route path="/orderItems" component={OrderItems}></Route>




      </main>
    </div>
    </BrowserRouter>
  );
}

export default App;
